// Analyze resource usage and generate alerts
// Get data directly from previous nodes (no Merge node needed)
const diskData = $('Query Disk Usage').first().json || {};
const cpuData = $('Query CPU Usage').first().json || {};
const memData = $('Query Memory Usage').first().json || {};

const THRESHOLDS = {
  disk: { warning: 85, critical: 95 },
  cpu: { warning: 80, critical: 95 },
  memory: { warning: 85, critical: 95 }
};

const alerts = [];
const metrics = [];

// Process disk usage
if (diskData?.data?.result) {
  for (const metric of diskData.data.result) {
    const instance = metric.metric?.instance || 'unknown';
    const mountpoint = metric.metric?.mountpoint || '/';
    const usage = parseFloat(metric.value?.[1]) || 0;

    metrics.push({ type: 'disk', instance, mountpoint, usage: usage.toFixed(1) });

    if (usage >= THRESHOLDS.disk.critical) {
      alerts.push({
        severity: 'critical',
        type: 'disk',
        message: `CRITICAL: Disk usage at ${usage.toFixed(1)}% on ${instance} (${mountpoint})`
      });
    } else if (usage >= THRESHOLDS.disk.warning) {
      alerts.push({
        severity: 'warning',
        type: 'disk',
        message: `WARNING: Disk usage at ${usage.toFixed(1)}% on ${instance} (${mountpoint})`
      });
    }
  }
}

// Process CPU usage
if (cpuData?.data?.result) {
  for (const metric of cpuData.data.result) {
    const instance = metric.metric?.instance || 'unknown';
    const usage = parseFloat(metric.value?.[1]) || 0;

    metrics.push({ type: 'cpu', instance, usage: usage.toFixed(1) });

    if (usage >= THRESHOLDS.cpu.critical) {
      alerts.push({
        severity: 'critical',
        type: 'cpu',
        message: `CRITICAL: CPU usage at ${usage.toFixed(1)}% on ${instance}`
      });
    } else if (usage >= THRESHOLDS.cpu.warning) {
      alerts.push({
        severity: 'warning',
        type: 'cpu',
        message: `WARNING: CPU usage at ${usage.toFixed(1)}% on ${instance}`
      });
    }
  }
}

// Process memory usage
if (memData?.data?.result) {
  for (const metric of memData.data.result) {
    const instance = metric.metric?.instance || 'unknown';
    const usage = parseFloat(metric.value?.[1]) || 0;

    metrics.push({ type: 'memory', instance, usage: usage.toFixed(1) });

    if (usage >= THRESHOLDS.memory.critical) {
      alerts.push({
        severity: 'critical',
        type: 'memory',
        message: `CRITICAL: Memory usage at ${usage.toFixed(1)}% on ${instance}`
      });
    } else if (usage >= THRESHOLDS.memory.warning) {
      alerts.push({
        severity: 'warning',
        type: 'memory',
        message: `WARNING: Memory usage at ${usage.toFixed(1)}% on ${instance}`
      });
    }
  }
}

return [{
  alerts,
  metrics,
  hasAlerts: alerts.length > 0,
  criticalCount: alerts.filter(a => a.severity === 'critical').length,
  warningCount: alerts.filter(a => a.severity === 'warning').length,
  timestamp: new Date().toISOString()
}];

// Format Slack message for resource alerts
const tz = $env.TIMEZONE || 'America/Chicago';
const formatTime = (date) => date.toLocaleString('en-US', { timeZone: tz, dateStyle: 'short', timeStyle: 'short' });

const data = $input.first().json;

const blocks = [
  {
    type: 'header',
    text: {
      type: 'plain_text',
      text: 'âš ï¸ Resource Usage Alert',
      emoji: true
    }
  },
  {
    type: 'section',
    text: {
      type: 'mrkdwn',
      text: `*Critical:* ${data.criticalCount} | *Warning:* ${data.warningCount}`
    }
  },
  { type: 'divider' }
];

// Group alerts by type
const alertsByType = {};
for (const alert of data.alerts) {
  if (!alertsByType[alert.type]) alertsByType[alert.type] = [];
  alertsByType[alert.type].push(alert);
}

for (const [type, alerts] of Object.entries(alertsByType)) {
  const icon = type === 'disk' ? 'ðŸ’¾' : type === 'cpu' ? 'ðŸ–¥ï¸' : 'ðŸ§ ';
  blocks.push({
    type: 'section',
    text: {
      type: 'mrkdwn',
      text: `${icon} *${type.toUpperCase()}*\n${alerts.map(a => a.message).join('\n')}`
    }
  });
}

blocks.push({
  type: 'context',
  elements: [{
    type: 'mrkdwn',
    text: `Checked at ${formatTime(new Date())}`
  }]
});

return [{
  blocks: blocks,
  text: `Resource Alert: ${data.criticalCount} critical, ${data.warningCount} warning`
}];

// Format alert for AI Operations Assistant
const slackData = $input.first().json;
const analysisData = $('Analyze Resources').first().json;

// Determine primary resource type from alerts
const resourceTypes = [...new Set(analysisData.alerts.map(a => a.type))];
const primaryType = resourceTypes[0] || 'resource';

// Build standardized alert payload
const alertPayload = {
  source: 'resource-usage-alerter',
  alert_type: `${primaryType}_threshold`,
  severity: analysisData.criticalCount > 0 ? 'critical' : 'warning',
  title: `Resource Usage Alert: ${analysisData.alerts.length} threshold(s) exceeded`,
  message: analysisData.alerts.map(a => a.message).join('; '),
  context: {
    resource_types: resourceTypes,
    metrics: analysisData.metrics,
    critical_count: analysisData.criticalCount,
    warning_count: analysisData.warningCount
  },
  timestamp: new Date().toISOString(),
  original_alert: analysisData
};

return [alertPayload];
